import React , {useState , useEffect}  from 'react'
import TestimonialApi from './TestimonialApi'
import Slide from './Slide'
import  './Testimonial.css'
function Testimonial() {
    const [data, setdata] = useState(TestimonialApi)
    const [index, setdindex] = useState(0)
    useEffect(() => {
        const lastIndex = data.length - 1
        if(index < 0){
            setdindex(lastIndex)
        }
        if(index > lastIndex){
            setdindex(0)
        }
      
      
    }, [index , data])

  useEffect(() => {
      
        let slider = setInterval(()=> {
            setdindex(index + 1)
        })
          
         return () => clearInterval(slider)
  }, [index])
  return (
    <>
    <section className="Testimonial" id="clients">
      <div className="container">
          <div className="heading text-center">
              <h4>WHAT CLIENTS SAY</h4>
              <h1>Testimonial</h1>
          </div>

          <div className="slide">
              {data.map((value , valueIndex) =>{
                  return   <Slide key={value.id} {...value} valueIndex={valueIndex} index={index} />
              })}
            
              <div className="slide_button">
                  <button className="btn-shadow prev_btn">
                      <i className="fas fa-arrow-left"></i>
                  </button>
                  <button className="btn-shadow next_btn">
                      <i className="fas fa-arrow-right"></i>
                  </button>
              </div>
          </div>
      </div>
    </section>
    </>
  )
}

export default Testimonial
